import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

import * as serviceWorker from './serviceWorker';

const TodoListItem = ({ todo, toggleCompleted, deleteTodo }) => (
  <li style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>
    <div>{todo.name}</div>
    <div>{todo.description}</div>
    <input
      type='checkbox'
      checked={todo.completed}
      onChange={toggleCompleted}
    />
    <button onClick={deleteTodo}>Delete Todo</button>
  </li>
);

class AddTodoForm extends React.Component {
  state = {
    value: ''
  };

  handleChange = event =>
    this.setState({
      value: event.target.value
    });
  render() {
    const { addTodoItem } = this.props;
    console.log(this.state.value);

    return (
      <form onSubmit={event => console.log(event)}>
        ;
        <input
          type='text'
          value={this.state.value}
          onChange={this.handleChange}
        />
        <button type='submit'>Add Todo</button>;
      </form>
    );
  }
}

class TodoList extends React.Component {
  state = {
    todos: [
      {
        description: 'Get out of bed',
        completed: true
      },
      {
        description: 'Brush teeth',
        completed: false
      },
      {
        description: 'Eat breakfast',
        completed: false
      }
    ]
  };

  addTodoItem = () => {
    const newTodo = {
      description: 'Eat lanch',
      completed: false
    };
    this.setState({
      todos: [...this.state.todos, newTodo]
    });
  };

  toggleCompleted = currentIndex => {
    this.setState({
      todos: this.state.todos.map((todo, index) => {
        if (currentIndex === index) {
          return {
            ...todo,
            completed: !todo.completed
          };
        } else {
          return todo;
        }
      })
    });
  };

  deleteTodo = currentIndex => {
    this.setState({
      todos: this.state.todos.filter((_todos, index) => currentIndex !== index)
    });
  };

  render() {
    const noTodos = !this.state.todos.length;
    return (
      <>
        {noTodos ? (
          'No items!'
        ) : (
          <ul>
            {this.state.todos.map((todo, index) => (
              <TodoListItem
                key={index}
                todo={todo}
                toggleCompleted={() => this.toggleCompleted(index)}
                deleteTodo={() => this.deleteTodo(index)}
              />
            ))}
          </ul>
        )}
        <AddTodoForm addTodoItem={this.addTodoItem} />
      </>
    );
  }
}

ReactDOM.render(
  <React.StrictMode>
    <h1>My todo list</h1>
    <TodoList />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
