import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

import * as serviceWorker from './serviceWorker';

const TodoListItem = (todo, toggleCompleted, deleteTodo) => (
  <li
    style={{ textDecoration: props.todo.completed ? 'line-through' : 'none' }}>
    <div>{todo.name}</div>
    <div>{todo.description}</div>
    <input type='checkbox' checked={todo.completed} onChange={toggleComplete} />
    <button onClick={this.deleteTodo}>Delete Todo</button>
  </li>
);

class TodoList extends React.Component {
  state = {
    todos: [
      {
        id: 1,
        description: 'Get out of bed',
        completed: false
      },
      {
        id: 2,
        description: 'Brush teeth',
        completed: false
      },
      {
        id: 3,
        description: 'Eat breakfast',
        completed: false
      }
    ]
  };

  addTodoItem = () => {
    const newTodo = {
      id: 4,
      description: 'eat lanch'
    };
    this.setState({
      todos: [...this.state.todos, newTodo]
    });
  };

  toggleComplete = currentIndex => {
    this.setState({
      todos: this.state.todos.map((todo, index) => {
        if (currentIndex === index) {
          return {
            ...todo,
            completed: !todo.completed
          };
        } else {
          return todo;
        }
      })
    });
  };

  deleteTodo = currentIndex => {
    this.setState({
      todos: this.todos.filter((todos, index) => currentIndex !== index)
    });
  };

  render() {
    const noTodos = !this.state.todos.length;
    return (
      <>
        {noTodos ? (
          'Not todos!'
        ) : (
          <ul>
            {this.state.todos.map((todo, index) => (
              <TodoListItem
                key={index}
                todo={todo}
                toggleComplete={() => this.toggleComplete(index)}
                deleteTodo={() => this.deleteTodo(index)}
              />
            ))}
          </ul>
        )}
        <button onClick={this.addTodoItem}>Add Todo</button>
      </>
    );
  }
}

ReactDOM.render(
  <React.StrictMode>
    <h1>My todo list</h1>
    <TodoList />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
